function updateYearElements(){var e=document.querySelectorAll(".year");e.forEach(function(e){e.textContent!==(new Date).getFullYear().toString()&&(e.textContent=(new Date).getFullYear())})}document.addEventListener("DOMContentLoaded",function(){setTimeout(function(){updateYearElements()},100);const e=new MutationObserver(function(){updateYearElements()});e.observe(document.body,{childList:!0,subtree:!0})});
