document.addEventListener("DOMContentLoaded",function(){setTimeout(function(){updateYearElements()},100);let e=new MutationObserver(function(){updateYearElements()});e.observe(document.body,{childList:!0,subtree:!0})});function updateYearElements(){document.querySelectorAll(".year").forEach(function(e){e.textContent!==new Date().getFullYear().toString()&&(e.textContent=new Date().getFullYear())})}
