function updateSlide(){slide.src=images[currentIndex],slideInterval=setInterval(autoSlide,5e3)}function autoSlide(){changeSlide(1)}function changeSlide(e){clearInterval(slideInterval),currentIndex=(currentIndex+e+images.length)%images.length,updateSlide(),slideInterval=setInterval(autoSlide,5e3)}function openLightbox(e){currentIndex=e,lightboxImg.src=images[currentIndex],lightbox.style.display="flex",clearInterval(slideInterval),updateSlide()}function changeImage(e){currentIndex=(currentIndex+e+images.length)%images.length,lightboxImg.src=images[currentIndex]}function closeLightbox(){lightbox.style.display="none",slideInterval=setInterval(autoSlide,5e3)}let currentIndex=0;const images=Array.from(document.querySelectorAll("#image-references img")).map(e=>e.src),slide=document.getElementById("slide"),lightbox=document.getElementById("lightbox"),lightboxImg=document.getElementById("lightboxImage");let slideInterval;updateSlide(),document.querySelectorAll("#image-references img").forEach((e,n)=>{e.onclick=(()=>openLightbox(n))});const magnifyingGlass=document.querySelector(".magnifying-glass");magnifyingGlass.addEventListener("click",()=>openLightbox(currentIndex));